SELECT nextval('SEQ_TIME_SEG_DEF') from dual
insert into TIME_SEG_DEF (create_date,status_date, version
SEG_ID SEG_NAME DATE_MODE TIME_MODE description STATUS_CD MAINTAIN_TYPE ORG_ID tenant_id OPERATE_CODE
) values(utc_timestamp() ,utc_timestamp(),utc_timestamp()
#segId# #segName# #dateMode# #timeMode# #description# #statusCd# #maintainType# #orgId# #tenantId# #operateCode#
)

