) values(utc_timestamp() ,utc_timestamp() ,utc_timestamp()
#pricePlanId# #cycleUnit# #cycleType# #cycleSyncFlag# #subEffectMode# #subDelayUnit# #subDelayCycle# #validUnit# #vaildType# #statusCd# #fixedExpireDate# #tenantId#
