sqlMap.select:
SELECT t1.*, t2.maind_id actTypeId, t2.cep_name actType FROM (SELECT t.so_nbr soNbr,i.main_flag mainFlag,i.prod_id prodId,i.prod_name prodName,i.act_type,t.tenant_id FROM prod_inst_info i, (SELECT b.order_item_id,a.so_nbr,a.tenant_id FROM cust_order_info a LEFT JOIN cust_order_item b ON a.order_id = b.order_id and a.tenant_id =#tenantId# and b.tenant_id=#tenantId# WHERE 1 = 1
a.order_id = #orderId#
) t WHERE i.order_item_id = t.order_item_id and i.tenant_id =#tenantId# and t.tenant_id=#tenantId#) t1 LEFT JOIN (SELECT * FROM main_data m WHERE m.mdt_cd = #CRMORDER_PRODUCT_ACT_TYPE# and m.tenant_id=#tenantId#) t2 ON t1.act_type = t2.cep_code and t1.tenant_id = #tenantId# and t2.tenant_id=#tenantId#

SELECT a.so_nbr soNbr, a.u_id uId, a.user_id userId, m.maind_id userTypeId, m.cep_name userType, a.company_name companyName, a.first_name firstName, a.middle_name middleName, a.last_name lastName, a.address_info addressInfo, a.phone phone FROM (SELECT t.so_nbr,p.* FROM prod_inst_user_info p, (SELECT r.u_id,t.so_nbr FROM prod_inst_user_rel r, (SELECT t1.prod_inst_id,t3.so_nbr,t1.tenant_id FROM prod_inst_info t1 LEFT JOIN cust_order_item t2 ON t1.order_item_id = t2.order_item_id and t1.tenant_id =#tenantId# and t2.tenant_id=#tenantId# LEFT JOIN cust_order_info t3 ON t2.order_id = t3.order_id and t3.tenant_id=#tenantId# WHERE 1 = 1
t3.order_id = #orderId#
) t WHERE r.prod_inst_id = t.prod_inst_id and r.tenant_id =#tenantId# and t.tenant_id=#tenantId# ) t WHERE p.u_id = t.u_id and p.tenant_id =#tenantId# ) a LEFT JOIN (SELECT * FROM main_data m WHERE m.mdt_cd = #CRMORDER_USER_TYPE_ID# and m.tenant_id=#tenantId#) m ON a.user_type = m.cep_code and a.tenant_id=#tenantId#
