sqlMap.select:
SELECT AA.*, ifnull(BB.PAGE_VIEW,0) PAGE_VIEW, ifnull(BB.COLLECT_CNT,0) COLLECT_CNT FROM ( SELECT A.* FROM PROD_OFFER A WHERE A.COOPERATION_TYPE = '11' AND A.STATUS_CD = '1000' AND A.TENANT_ID = #tenantId# AND A.exp_date >= NOW()
A.PROD_OFFER_ID in (select PROD_OFFER_ID from offer_catalog_location t1 where t1.tenant_id = #tenantId# and t1.dir_id in ( select dir_id from directory where (dir_id = #dirId# or fa_dir_id = #dirId#) and tenant_id = 1 and dir_state='A' and dir_type=3 ) )
) AA LEFT JOIN ITEM_CNT BB ON AA.PROD_OFFER_ID = BB.item_code AND BB.tenant_id = #tenantId# AND BB.item_type = 2

SELECT AA.*, ifnull(BB.PAGE_VIEW,0) PAGE_VIEW, ifnull(BB.COLLECT_CNT,0) COLLECT_CNT FROM ( SELECT A.* FROM PROD_OFFER A WHERE A.COOPERATION_TYPE = '11' AND A.STATUS_CD = '1000' AND A.TENANT_ID = #tenantId# AND A.exp_date >= NOW()
A.PROD_OFFER_ID IN (select PROD_OFFER_ID from offer_catalog_location t1 where t1.tenant_id = #tenantId# and t1.dir_id in ( select dir_id from directory where (dir_id = #dirId# or fa_dir_id = #dirId#) and tenant_id = 1 and dir_state='A' and dir_type=3 ) )
) AA LEFT JOIN ITEM_CNT BB ON AA.PROD_OFFER_ID = BB.item_code AND BB.tenant_id = #tenantId# and BB.item_type = 2 left join item_sort its on AA.PROD_OFFER_ID = its.item_code and its.item_type = 2 and its.sort_type = 1 and its.tenant_id = #tenantId# order by sort_no desc,prod_offer_id desc limit #startRow#,#pageSize#

SELECT AA.*, ifnull(BB.PAGE_VIEW,0) PAGE_VIEW, ifnull(BB.COLLECT_CNT,0) COLLECT_CNT FROM ( SELECT A.* FROM PROD_OFFER A WHERE A.COOPERATION_TYPE = '11' AND A.STATUS_CD = '1000' AND A.TENANT_ID = #tenantId# AND A.exp_date >= NOW()
A.PROD_OFFER_ID in (select PROD_OFFER_ID from offer_catalog_location t1 where t1.tenant_id = #tenantId# and t1.dir_id in ( select dir_id from directory where (dir_id = #dirId# or fa_dir_id = #dirId#) and tenant_id = 1 and dir_state='A' and dir_type=3 ) )
) AA LEFT JOIN ITEM_CNT BB ON AA.PROD_OFFER_ID = BB.item_code AND BB.tenant_id = #tenantId# ORDER BY AA.EFF_DATE DESC,AA.PROD_OFFER_ID DESC limit #startRow#,#pageSize#

SELECT COUNT(1) prod_offer_cnt FROM ( SELECT A.* FROM PROD_OFFER A WHERE A.COOPERATION_TYPE = '11' AND A.STATUS_CD = '1000' AND A.TENANT_ID = #tenantId# AND A.exp_date >= NOW()
A.prod_offer_id in (select prod_offer_id from offer_catalog_location t1 where t1.tenant_id = #tenantId# and t1.dir_id in ( select dir_id from directory where (dir_id = #dirId# or fa_dir_id = #dirId#) and tenant_id = 1 and dir_state='A' and dir_type=3 ) )
) AA
