sqlMap.select:
SELECT * FROM (SELECT ROW_NUMBER() OVER( ORDER BY prodOfferId DESC ) rn , newtab.* FROM ( SELECT P.PROD_OFFER_ID as prodOfferId ,P.OFFER_NBR as offerNbr ,P.PROD_OFFER_NAME as prodOfferName , P.PROD_OFFER_DESC as prodOfferDesc , P.STATUS_CD as statusCd FROM PROD_OFFER P,PROD_OFFER_DISTRIBUTOR A WHERE P.PROD_OFFER_ID = A.PROD_OFFER_ID AND P.STATUS_CD = '1000'
STATUS_CD = #prodOfferStatusCd# PROD_OFFER_NAME LIKE concat('%',#prodOfferName#,'%') A.DISTRIBUTOR_ID = #orgCode# P.tenant_id=#tenantId# A.tenant_id=#tenantId#
) newtab ) WHERE rn BETWEEN #pro# AND #end#

SELECT * FROM (SELECT ROW_NUMBER() OVER( ORDER BY productId DESC ) rn , newtab.* FROM ( SELECT P.PRODUCT_ID as productId ,P.PRODUCT_NBR as productNbr ,P.PRODUCT_NAME as productName , P.PRODUCT_DESC as productDesc ,P.PROD_FUNC_TYPE as prodFuncType ,P.PRODUCT_TYPE as productType ,P.VALUE_ADDED_FLAG as valueAddedFlag ,P.EFF_DATE as effDate ,P.EXP_DATE as expDate ,P.STATUS_CD as statusCd FROM PRODUCT P,PRODUCT_DISTRIBUTOR A WHERE P.PRODUCT_ID = A.PRODUCT_ID AND P.STATUS_CD = '1000'
P.STATUS_CD = #productStatusCd# P.PRODUCT_NAME LIKE concat('%',#productName#,'%') A.DISTRIBUTOR_ID = #orgCode# P.tenant_id=#tenantId# A.tenant_id=#tenantId#
) newtab ) WHERE rn BETWEEN #pro# AND #end#
