SELECT COUNT(1) FROM ( SELECT msg.MSG_ID , msg.tenant_id FROM MESSAGE msg, MSG_RECIPIENT_REL b WHERE msg.STATUS_CD='1000' AND msg.MSG_REC_TYPE = 1 AND b.REC_ID like ('%'||#roleId#||'%') AND msg.MSG_ID = b.MSG_ID and MSG_BEGINN_DATE <= TO_CHAR(SYS_EXTRACT_UTC(current_timestamp)+0 ,'%Y-%m-%d %h:%i:%s') AND MSG_END_DATE >= TO_CHAR(SYS_EXTRACT_UTC(current_timestamp)+0 ,'%Y-%m-%d %h:%i:%s')
msg.MST_TYPE = #msgType# (msg.MSG_TITLE like ('%'||#query#||'%') or msg.MSG_SUBTITLE like ('%'||#query#||'%') or msg.MSG_CONTENT like ('%'||#query#||'%')) msg.tenant_id=#tenantId#
) parama where not exists ( select * from (SELECT MSG_ID,tenant_id FROM MESSAGE_RECIPENT c WHERE c.REC_ID = #orgId# and c.tenant_id=#tenantId#) paramb where parama.msg_id = paramb.msg_id and parama.tenant_id =#tenantId# and paramb.tenant_id=#tenantId#)

select b.* from (select rownum RECNO,a.* from ( SELECT parama.*,TO_CHAR(MSG_BEGINN_DATE,'yyyy-mm-dd') FORMAT_BEGIN_DATE,TO_CHAR(MSG_END_DATE,'yyyy-mm-dd') FORMAT_END_DATE FROM ( SELECT msg.* FROM MESSAGE msg, MSG_RECIPIENT_REL b WHERE msg.STATUS_CD='1000' AND msg.MSG_REC_TYPE = 1 AND b.REC_ID like ('%'||#roleId#||'%') AND msg.MSG_ID = b.MSG_ID and MSG_BEGINN_DATE <= SYS_EXTRACT_UTC(current_timestamp)+0 AND MSG_END_DATE >= SYS_EXTRACT_UTC(current_timestamp)
msg.MSG_TYPE = #msgType# (msg.MSG_TITLE like ('%'||#query#||'%') or msg.MSG_SUBTITLE like ('%'||#query#||'%') or msg.MSG_CONTENT like ('%'||#query#||'%')) msg.tenant_id=#tenantId#
) parama where not exists ( select * from (SELECT MSG_ID,tenant_id FROM MESSAGE_RECIPENT c WHERE c.REC_ID = #orgId# and c.tenant_id=#tenantId#) paramb where parama.msg_id = paramb.msg_id and parama.tenant_id =#tenantId# and paramb.tenant_id=#tenantId#) ORDER BY MSG_PRIORITY asc ,MSG_ID desc ) a where ROWNUM <= (#begin# + #end# ) ORDER BY ROWNUM ) b where RECNO BETWEEN (#begin#+1) AND (#begin# + #end#)

SELECT count(1) FROM( SELECT * FROM ( SELECT msg.MSG_ID,msg.tenant_id FROM MESSAGE msg, MSG_RECIPIENT_REL b WHERE msg.STATUS_CD='1000' AND msg.MSG_REC_TYPE = 2 AND b.REC_ID = #orgId# AND MSG_BEGINN_DATE <= SYS_EXTRACT_UTC(current_timestamp)+0 AND MSG_END_DATE >= SYS_EXTRACT_UTC(current_timestamp)+0 AND msg.MSG_ID = b.MSG_ID
msg.MSG_TYPE = #msgType# (msg.MSG_TITLE like ('%'||#query#||'%') or msg.MSG_SUBTITLE like ('%'||#query#||'%') or msg.MSG_CONTENT like ('%'||#query#||'%')) msg.tenant_id=#tenantId#
) parama where not exists ( select * from (SELECT MSG_ID,tenant_id FROM MESSAGE_RECIPENT c WHERE c.REC_ID = #orgId# AND c.tenant_id=#tenantId# AND c.STATUS_CD IN ('1600','1500') ) paramb where parama.msg_id = paramb.msg_id and parama.tenant_id =#tenantId# and paramb.tenant_id=#tenantId#) ) paramc

select b.* from (select rownum RECNO,a.* from ( SELECT paramc.*,TO_CHAR(paramc.MSG_BEGINN_DATE,'yyyy-mm-dd') FORMAT_BEGIN_DATE,TO_CHAR(paramc.MSG_END_DATE,'yyyy-mm-dd') FORMAT_END_DATE FROM ( SELECT * FROM ( SELECT msg.* FROM MESSAGE msg, MSG_RECIPIENT_REL b WHERE msg.STATUS_CD='1000' AND msg.MSG_REC_TYPE = 2 AND b.REC_ID = #orgId# AND MSG_BEGINN_DATE <= SYS_EXTRACT_UTC(current_timestamp)+0 AND MSG_END_DATE >= SYS_EXTRACT_UTC(current_timestamp)+0 AND msg.MSG_ID = b.MSG_ID AND msg.tenant_id = b.tenant_id
msg.MSG_TYPE = #msgType# (msg.MSG_TITLE like ('%'||#query#||'%') or msg.MSG_SUBTITLE like ('%'||#query#||'%') or msg.MSG_CONTENT like ('%'||#query#||'%')) msg.tenant_id=#tenantId#
) parama where not exists ( select * from (SELECT MSG_ID,c.tenant_id FROM MESSAGE_RECIPENT c WHERE c.REC_ID = #orgId# AND c.tenant_id=#tenantId# AND c.STATUS_CD IN ('1600','1500')) paramb where parama.msg_id = paramb.msg_id and parama.tenant_id =#tenantId# and paramb.tenant_id=#tenantId#) ) paramc ORDER BY paramc.MSG_PRIORITY asc ,paramc.MSG_ID desc ) a where ROWNUM <= (#begin# + #end# ) ORDER BY ROWNUM ) b where RECNO BETWEEN (#begin#+1) AND (#begin# + #end#)

