sqlMap.select:
SELECT * FROM (SELECT ROW_NUMBER() OVER( ORDER BY PROD_OFFER_ID DESC ) rn , newtab.* FROM ( SELECT T.PROD_OFFER_ID, T.PROD_OFFER_NAME, T.OFFER_NBR, A.NAME, T.PROD_OFFER_DESC, TT.STATUS_CD, TT.AUDIT_FLOW_ID FROM PROD_OFFER T,PROD_OFFER_CHANNEL TT, CHANNEL TTT, PROD_OFFER_CHANNEL_TYPE TTTT, ORG A WHERE T.OFFER_PROVIDER_ID = #OFFER_PROVIDER_ID# AND T.PROD_OFFER_ID = TT.PROD_OFFER_ID AND TT.CHANNEL_ID = TTT.CHANNEL_ID AND TT.STATUS_CD = #STATUS_CD# AND TTT.STATUS_CD = '1000' AND T.PROD_OFFER_ID = TTTT.PROD_OFFER_ID AND TTTT.CHANNEL_TYPE = '20' AND T.OFFER_PROVIDER_ID = A.ORG_ID AND T.STATUS_CD = '1000' AND T.TENANT_ID=#tenantId# AND TT.TENANT_ID=#tenantId# AND TTT.TENANT_ID=#tenantId# AND TTTT.TENANT_ID=#tenantId# AND A.TENANT_ID=#tenantId#
T.PROD_OFFER_NAME = #PROD_OFFER_NAME#
) newtab ) WHERE rn BETWEEN #pro# AND #end#

SELECT * FROM (SELECT ROW_NUMBER() OVER( ORDER BY PROD_OFFER_ID DESC ) rn , newtab.* FROM ( SELECT T.PROD_OFFER_ID, A.NAME, T.PROD_OFFER_NAME, T.OFFER_NBR, T.INNER_OFFER_NAME, T.PROD_OFFER_DESC FROM PROD_OFFER T, PROD_OFFER_CHANNEL_TYPE TTT, ORG A WHERE T.OFFER_PROVIDER_ID = #OFFER_PROVIDER_ID# AND T.COOPERATION_TYPE = #COOPERATION_TYPE# AND T.PROD_OFFER_ID = TTT.PROD_OFFER_ID AND TTT.CHANNEL_TYPE = '20' AND T.OFFER_PROVIDER_ID = A.ORG_ID AND T.STATUS_CD = '1000' AND NOT EXISTS ( SELECT (1) FROM PROD_OFFER_CHANNEL TT WHERE T.PROD_OFFER_ID = TT.PROD_OFFER_ID AND TT.STATUS_CD IN ('1000','1299') ) AND T.TENANT_ID=#tenantId# AND TTT.TENANT_ID=#tenantId# AND A.TENANT_ID=#tenantId#
T.PROD_OFFER_NAME = #PROD_OFFER_NAME#
) newtab ) WHERE rn BETWEEN #pro# AND #end#
