SELECT seq_pricing_plan.NEXTVAL from dual
insert into pricing_plan (create_date,status_date, status_cd
pricing_info_id pricing_classify pricing_type pricing_desc beyond_desc price_sign price_cha eff_date exp_date PRICING_NAME is_main billing_priority license_nbr applic_type cycle_type cycle_unit
) values(SYS_EXTRACT_UTC(current_timestamp)+0 ,SYS_EXTRACT_UTC(current_timestamp)+0,'10'
#pricingInfoId# #pricingClassify# #pricingType# #pricingDesc# #beyondDesc# #priceSign# #priceCha# #effDate# #expDate# #pricingName# #isMain# #billingPriority# #licenseNbr# #applicType# #cycletype# #cycleUnit#
)

SELECT seq_prod_offer_pricing.NEXTVAL from dual
insert into prod_offer_pricing (CREATE_DATE, STATUS_DATE ,STATUS_CD
PROD_OFFER_PRICING_ID PROD_OFFER_ID PRICING_INFO_ID PRICING_TARGET_ID PRICING_TYPE
) values(SYS_EXTRACT_UTC(current_timestamp)+0 , SYS_EXTRACT_UTC(current_timestamp)+0 ,'10'
#prodOfferPricingId# #prodOfferId# #pricingInfoId# #pricingTargetId# #pricingType#
)

